import { KPICards } from '@/components/dashboard/kpi-cards';
import { RecentSamples } from '@/components/dashboard/recent-samples';
import { QuickActions } from '@/components/dashboard/quick-actions';
import { SampleVolumeChart, TestDistributionChart } from '@/components/dashboard/charts';
import { Button } from '@/components/ui/button';
import { Download, Plus } from 'lucide-react';
import { Link } from 'wouter';

export default function Dashboard() {
  const currentDate = new Date().toLocaleDateString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });

  const currentTime = new Date().toLocaleTimeString('en-US', {
    hour: 'numeric',
    minute: '2-digit',
    hour12: true,
  });

  return (
    <div className="p-4 sm:p-6 lg:p-8">
      {/* Header */}
      <div className="mb-8">
        <div className="md:flex md:items-center md:justify-between">
          <div className="min-w-0 flex-1">
            <h2 className="text-2xl font-bold leading-7 text-gray-900 sm:truncate sm:text-3xl sm:tracking-tight dark:text-white">
              Laboratory Dashboard
            </h2>
            <div className="mt-1 flex flex-col sm:mt-0 sm:flex-row sm:flex-wrap sm:space-x-6">
              <div className="mt-2 flex items-center text-sm text-gray-500 dark:text-gray-400">
                <span>{currentDate}</span>
              </div>
              <div className="mt-2 flex items-center text-sm text-gray-500 dark:text-gray-400">
                <span>{currentTime}</span>
              </div>
            </div>
          </div>
          <div className="mt-4 flex md:ml-4 md:mt-0 gap-3">
            <Button 
              variant="outline" 
              onClick={() => {
                console.log('Exporting dashboard report...');
                const reportData = {
                  type: 'dashboard_summary',
                  date: new Date().toLocaleDateString(),
                  stats: {
                    dailySamples: 0,
                    resultsReady: 0,
                    pendingReview: 0,
                    overdueResults: 0
                  }
                };

                // Create CSV content
                const csvContent = `Dashboard Report - ${reportData.date}\n` +
                  `Daily Samples,${reportData.stats.dailySamples}\n` +
                  `Results Ready,${reportData.stats.resultsReady}\n` +
                  `Pending Review,${reportData.stats.pendingReview}\n` +
                  `Overdue Results,${reportData.stats.overdueResults}\n`;

                // Create and download file
                const blob = new Blob([csvContent], { type: 'text/csv' });
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.style.display = 'none';
                a.href = url;
                a.download = `dashboard-report-${new Date().toISOString().split('T')[0]}.csv`;
                document.body.appendChild(a);
                a.click();
                window.URL.revokeObjectURL(url);
                document.body.removeChild(a);

                alert('Dashboard report exported successfully!');
              }}
            >
              <Download className="mr-2 h-4 w-4" />
              Export Report
            </Button>
            <Link href="/sampling">
              <Button>
                <Plus className="mr-2 h-4 w-4" />
                New Sample
