import { useState } from 'react';
import { usePatients } from '@/hooks/use-patients';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { Switch } from '@/components/ui/switch';
import { Loader2, User, Plus, Trash2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface Medication {
  name: string;
  type: string;
  dose: string;
  duration: string;
}

export function PatientRegistration() {
  const { createPatient, isCreating } = usePatients();
  const { toast } = useToast();

  const [form, setForm] = useState({
    patientId: '',
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    phoneNumber: '',
    email: '',
    address: '',
    emergencyContact: '',
    insuranceNumber: '',
    nationalId: '',
    treatingDoctor: '',
    landline: '',
    fastingHours: '',
    isFasting: false,
    isDiabetic: false,
    isOnBloodThinner: false,
    isOnAntibiotics: false,
    isOnThyroidMedication: false,
    isOnKidneyTreatment: false,
    isOnLiverTreatment: false,
    isOnCholesterolMedication: false,
    isOnCortisone: false,
    hadContrastScan: false,
    hadBloodTransfusion: false,
    bloodTransfusionDate: '',
    hadSurgeries: false,
    hadChemoRadiotherapy: false,
    lastMenstrualPeriod: '',
    isPregnant: false,
    requiredTests: '',
    isOnIronVitamins: false,
    ironVitaminsDose: '',
    ironVitaminsDuration: '',
  });

  const [medications, setMedications] = useState<Medication[]>([
    { name: '', type: '', dose: '', duration: '' }
  ]);

  const generatePatientId = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    const time = String(now.getTime()).slice(-4);
    return `PAT-${year}${month}${day}-${time}`;
  };

  const addMedication = () => {
    setMedications([...medications, { name: '', type: '', dose: '', duration: '' }]);
  };

  const removeMedication = (index: number) => {
    setMedications(medications.filter((_, i) => i !== index));
  };

  const updateMedication = (index: number, field: keyof Medication, value: string) => {
    const updated = [...medications];
    updated[index][field] = value;
    setMedications(updated);
  };

  const clearForm = () => {
    setForm({
      patientId: '',
      firstName: '',
      lastName: '',
      dateOfBirth: '',
      gender: '',
      phoneNumber: '',
      email: '',
      address: '',
      emergencyContact: '',
      insuranceNumber: '',
      nationalId: '',
      treatingDoctor: '',
      landline: '',
      fastingHours: '',
      isFasting: false,
      isDiabetic: false,
      isOnBloodThinner: false,
      isOnAntibiotics: false,
      isOnThyroidMedication: false,
      isOnKidneyTreatment: false,
      isOnLiverTreatment: false,
      isOnCholesterolMedication: false,
      isOnCortisone: false,
      hadContrastScan: false,
      hadBloodTransfusion: false,
      bloodTransfusionDate: '',
      hadSurgeries: false,
      hadChemoRadiotherapy: false,
      lastMenstrualPeriod: '',
      isPregnant: false,
      requiredTests: '',
      isOnIronVitamins: false,
      ironVitaminsDose: '',
      ironVitaminsDuration: '',
    });
    setMedications([{ name: '', type: '', dose: '', duration: '' }]);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!form.patientId) {
      setForm(prev => ({ ...prev, patientId: generatePatientId() }));
    }

    const patientData = {
      ...form,
      dateOfBirth: new Date(form.dateOfBirth),
      gender: form.gender as 'male' | 'female' | 'other' | 'unknown',
      fastingHours: form.fastingHours ? parseInt(form.fastingHours) : null,
      bloodTransfusionDate: form.bloodTransfusionDate ? new Date(form.bloodTransfusionDate) : null,
      lastMenstrualPeriod: form.lastMenstrualPeriod ? new Date(form.lastMenstrualPeriod) : null,
      medications: medications.filter(med => med.name.trim() !== ''),
    };

    createPatient(patientData, {
      onSuccess: () => {
        clearForm();
        toast({
          title: "Patient registered successfully",
          description: `${form.firstName} ${form.lastName} has been added to the system.`,
        });
      },
      onError: (error) => {
        console.error('Registration failed:', error);
        toast({
          title: "Registration failed",
          description: "Please check all fields and try again.",
          variant: "destructive",
        });
      },
    });
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <User className="h-5 w-5" />
          Patient Registration
        </CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-8">
          {/* Basic Information */}
          <div className="space-y-4">
            <h3 className="text-lg font-medium">Basic Information</h3>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <Label htmlFor="patientId">Patient ID</Label>
                <Input
                  id="patientId"
                  value={form.patientId}
                  onChange={(e) => setForm(prev => ({ ...prev, patientId: e.target.value }))}
                  placeholder="Auto-generated if empty"
                  disabled={isCreating}
                />
              </div>

              <div>
                <Label htmlFor="nationalId">National ID</Label>
                <Input
                  id="nationalId"
                  value={form.nationalId}
                  onChange={(e) => setForm(prev => ({ ...prev, nationalId: e.target.value }))}
                  placeholder="National ID number"
                  disabled={isCreating}
                />
              </div>

              <div>
                <Label htmlFor="insuranceNumber">Insurance Number</Label>
                <Input
                  id="insuranceNumber"
                  value={form.insuranceNumber}
                  onChange={(e) => setForm(prev => ({ ...prev, insuranceNumber: e.target.value }))}
                  placeholder="Insurance number"
                  disabled={isCreating}
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="firstName">First Name *</Label>
                <Input
                  id="firstName"
                  required
                  value={form.firstName}
                  onChange={(e) => setForm(prev => ({ ...prev, firstName: e.target.value }))}
                  placeholder="First name"
                  disabled={isCreating}
                />
              </div>

              <div>
                <Label htmlFor="lastName">Last Name *</Label>
                <Input
                  id="lastName"
                  required
                  value={form.lastName}
                  onChange={(e) => setForm(prev => ({ ...prev, lastName: e.target.value }))}
                  placeholder="Last name"
                  disabled={isCreating}
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="dateOfBirth">Date of Birth *</Label>
                <Input
                  id="dateOfBirth"
                  type="date"
                  required
                  value={form.dateOfBirth}
                  onChange={(e) => setForm(prev => ({ ...prev, dateOfBirth: e.target.value }))}
                  disabled={isCreating}
                />
              </div>

              <div>
                <Label htmlFor="gender">Gender *</Label>
                <Select value={form.gender} onValueChange={(value) => setForm(prev => ({ ...prev, gender: value }))}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select gender" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="male">Male</SelectItem>
                    <SelectItem value="female">Female</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                    <SelectItem value="unknown">Prefer not to say</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="treatingDoctor">Treating Doctor</Label>
              <Input
                id="treatingDoctor"
                value={form.treatingDoctor}
                onChange={(e) => setForm(prev => ({ ...prev, treatingDoctor: e.target.value }))}
                placeholder="Treating doctor name"
                disabled={isCreating}
              />
            </div>
          </div>

          <Separator />

          {/* Contact Information */}
          <div className="space-y-4">
            <h3 className="text-lg font-medium">Contact Information</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="phoneNumber">Mobile</Label>
                <Input
                  id="phoneNumber"
                  type="tel"
                  value={form.phoneNumber}
                  onChange={(e) => setForm(prev => ({ ...prev, phoneNumber: e.target.value }))}
                  placeholder="Mobile number"
                  disabled={isCreating}
                />
              </div>

              <div>
                <Label htmlFor="landline">Landline</Label>
                <Input
                  id="landline"
                  type="tel"
                  value={form.landline}
                  onChange={(e) => setForm(prev => ({ ...prev, landline: e.target.value }))}
                  placeholder="Landline number"
                  disabled={isCreating}
                />
              </div>
            </div>

            <div>
              <Label htmlFor="email">Email</Label>
